#define SCAN_WIDTH 28
#define SCAN_HEIGHT 28
const uint sw = SCAN_WIDTH;
const uint sh = SCAN_HEIGHT;
const uint sw2 = sw/2;
const uint sh2 = sh/2;
const uint slc = sw*sh;
const uint slall = slc*3;

uint x=0, y=0;

float input[slall] = {0};
    double r[slc] = {0};
    double g[slc] = {0};
    double b[slc] = {0};

Display *d;
int si;
Window twin;
GC gc = 0;
